mapper.select:
select
  id, section_code, section_name, section_alphbet, order_num, contact, contact_mobile, check_type, del_flag, create_id, create_time, update_id, update_time, delete_id, delete_time, department_id
from t_section_office


        and id = #{id}


        and section_code = #{sectionCode}


        and section_name = #{sectionName}


        and section_alphbet = #{sectionAlphbet}


        and order_num = #{orderNum}


        and contact = #{contact}


        and contact_mobile = #{contactMobile}


        and check_type = #{checkType}


        and del_flag = #{delFlag}


        and create_id = #{createId}


        and create_time = #{createTime}


        and update_id = #{updateId}


        and update_time = #{updateTime}


        and delete_id = #{deleteId}


        and delete_time = #{deleteTime}


        and department_id = #{departmentId}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from t_section_office


        and id = #{id}


        and section_code = #{sectionCode}


        and section_name = #{sectionName}


        and section_alphbet = #{sectionAlphbet}


        and order_num = #{orderNum}


        and contact = #{contact}


        and contact_mobile = #{contactMobile}


        and check_type = #{checkType}


        and del_flag = #{delFlag}


        and create_id = #{createId}


        and create_time = #{createTime}


        and update_id = #{updateId}


        and update_time = #{updateTime}


        and delete_id = #{deleteId}


        and delete_time = #{deleteTime}


        and department_id = #{departmentId}
