mapper.select:
select
  id, office_id, hazard_factors_text, inspect_type, hazard_factors, type, status, content, order_num, del_flag, create_id, create_time, update_id, update_time, delete_id, delete_time, work_state_code, work_state_text
from t_office_term


        and id = #{id}


        and office_id = #{officeId}


        and hazard_factors_text = #{hazardFactorsText}


        and inspect_type = #{inspectType}


        and hazard_factors = #{hazardFactors}


        and type = #{type}


        and status = #{status}


        and content = #{content}


        and order_num = #{orderNum}


        and del_flag = #{delFlag}


        and create_id = #{createId}


        and create_time = #{createTime}


        and update_id = #{updateId}


        and update_time = #{updateTime}


        and delete_id = #{deleteId}


        and delete_time = #{deleteTime}


        and work_state_code = #{workStateCode}


        and work_state_text = #{workStateText}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from t_office_term


        and id = #{id}


        and office_id = #{officeId}


        and hazard_factors_text = #{hazardFactorsText}


        and inspect_type = #{inspectType}


        and hazard_factors = #{hazardFactors}


        and type = #{type}


        and status = #{status}


        and content = #{content}


        and order_num = #{orderNum}


        and del_flag = #{delFlag}


        and create_id = #{createId}


        and create_time = #{createTime}


        and update_id = #{updateId}


        and update_time = #{updateTime}


        and delete_id = #{deleteId}


        and delete_time = #{deleteTime}


        and work_state_code = #{workStateCode}


        and work_state_text = #{workStateText}
